import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      todos: [],
      todo: ''
    };
  }
  updateToDo = e => {
    const task = e.target.value;
    this.setState({
      todo: task
    });
  };

  render() {
    return (
      <div className='App'>
        <h1>Todo List App</h1>
        <input onChange={this.updateToDo} type='text' />
      </div>
    );
  }
}

export default App;
