import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      todo: ""
    };
  }
  updateInput = (e) => {
    this.setState({
      todo: e.target.value,
    })
    console.log(this.state.todo)
  }
  render() {
    return (
      <div className="App">
        <input type="text" onChange={this.updateInput}/>
        <button>Add Task</button>
      </div>
    );
  }
}

export default App;
