import React, { Component } from 'react';
import './App.css';
import Todo from './components/Todo/Todo';
import Todos from './components/Todo/Todos';
import Radium from 'radium';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      todos: [],
      todo: '',
      completions: []
    };
  }
  updateToDo = e => {
    const task = e.target.value;
    this.setState({
      todo: task
    });
  };
  addTask = () => {
    this.setState({
      todos: [...this.state.todos, this.state.todo],
      completions: [...this.state.completions, false]
    });
    console.log(this.state.todos);
    console.log(this.state.completions);
  };
  deleteTask = index => {
    this.setState({
      todos: this.state.todos.filter((_, i) => i !== index)
    });
  };
  isCompleted = (event, index) => {
    this.setState(prevState => ({
      completions: {
        ...prevState.completions,
        [prevState.completions[index]]: event.target.value
      }
    }));
  };

  render() {
    const todosTasks = this.state.todos.map((todo, index) => {
      return (
        <Todos
          key={index}
          todo={todo}
          deleteTask={() => this.deleteTask(index)}
          completed={this.isCompleted}
          sendIsCompleted={() => this.state.isCompleted(index)}
        />
      );
    });
    return (
      <div className='App'>
        <h1>Todo List App</h1>
        <Todo changed={this.updateToDo} submit={this.addTask} />
        {todosTasks}
      </div>
    );
  }
}

export default App;
