import React from 'react';
import Radium from 'radium';

const Todos = props => {
  console.log('from todos...', props.sendIsCompleted);

  return (
    <div>
      <li
        style={
          /*(*/ listStyle
          /*props.sendIsCompleted ? { textDecoration: 'line-through' } : null)*/
        }
      >
        {props.todo}
      </li>
      <button style={taskButtons} onClick={props.completed}>
        Mark Completed
      </button>
      <button style={taskButtons} onClick={props.deleteTask}>
        Delete Task
      </button>
    </div>
  );
};

const listStyle = {
  listStyleType: 'none',
  paddingLeft: '0',
  padding: '15px',
  marginBottom: '15px',
  borderRadius: '5px',
  width: '250px',
  backgroundColor: 'rgba(255,255,255,.5)',
  margin: 'auto'
};

const taskButtons = {
  backgroundColor: '#0066FF',
  padding: '10px',
  fontSize: '16px',
  border: '2px solid #FFF',
  marginLeft: '15px',
  borderRadius: '8px',
  cursor: 'pointer',
  border: '2px solid #003399',
  ':hover': {
    backgroundColor: '#003399'
  },
  ':focus': {
    outline: 0
  }
};

export default Radium(Todos);
