import React, { Component } from 'react';
import './App.css';
import Todo from './components/Todo/Todo';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      todos: [],
      todo: ''
    };
  }
  updateToDo = e => {
    const task = e.target.value;
    this.setState({
      todo: task
    });
    console.log(this.state.todo);
  };
  addTask = () => {
    this.setState({
      todos: [...this.state.todos.push(this.state.todo)]
    });
    console.log(this.state.todos);
  };

  render() {
    return (
      <div className='App'>
        <h1>Todo List App</h1>
        <Todo changed={this.updateToDo} submit={this.addTask} />
      </div>
    );
  }
}

export default App;
